import styled from "styled-components";
import Button from "@material-ui/core/Button";
import Container from "@material-ui/core/Container";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import React from "react";
import TextField from "@material-ui/core/TextField";
import {makeStyles} from "@material-ui/styles";

export function TextInput({...props}) {
    const classes = makeStyles({
        cssLabel: {
            color: 'green',
        },

        cssOutlinedInput: {
            "&:not(hover):not($disabled):not($cssFocused):not($error) $notchedOutline": {
                // borderColor: "white" //default
            },
            "&:hover:not($disabled):not($cssFocused):not($error) $notchedOutline": {
                // borderColor: "blue" //hovered
            },
            "&$cssFocused $notchedOutline": {
                borderColor: "purple" //focused
            }
        },
        notchedOutline: {},
        cssFocused: {},
        error: {},
        disabled: {}
    })()

    return (
        <TextField
            InputLabelProps={{
                classes: {
                    root: classes.cssLabel,
                    focused: classes.cssFocused,
                },
            }}
            InputProps={{
                classes: {
                    root: classes.cssOutlinedInput,
                    focused: classes.cssFocused,
                    notchedOutline: classes.notchedOutline,
                },
            }}
            {...props}
        />
    )
}

export function Title({text, ...props}) {
    return (
        <Typography component="h1" variant="h5" align="center" color="textPrimary">
            {text}
        </Typography>
    )
}

export const FormWrapper = styled(Container)`
        width: auto;
        margin-left: ${({theme}) => theme.spacing(2)}px;
        margin-right: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(2) * 2)} {
            width: 600px;
            margin-left: auto;
            margin-right: auto;
        };
`

export const InnerFormSpacer = styled(Paper)`
        margin-bottom: ${({theme}) => theme.spacing(3)}px;
        padding: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(3) * 2)} {
            margin-bottom: ${({theme}) => theme.spacing(6)}px;
            padding: ${({theme}) => theme.spacing(3)}px;
        },
`

export const Btn = styled(Button)`
    margin-top: ${({theme}) => theme.spacing(3)}px;
    margin-left: ${({theme}) => theme.spacing(1)}px;
`

export const ButtonsGroup = styled.div`
        display: flex;
        justify-content: flex-start;
                
        ${(props) => props.left && `justify-content: flex-start;`}
        ${(props) => props.center && `justify-content: center;`}
        ${(props) => props.right && `justify-content: flex-end;`}
`
