export const LOGIN = 'LOGIN'
export const LOGOUT = 'LOGOUT'

const ERR = ''

export const login = () => {
    return async (dispatch) => {
        return await new Promise((resolve, reject) => {
            if (ERR) reject(ERR)
            resolve(true)
        }).then(result => {
            dispatch({
                type: LOGOUT,
                payload: result,
            })
            return result
        }).catch(err => {
            dispatch({
                type: LOGOUT,
                payload: false,
            })
            return err
        })
    }
}

export function logout() {
    return async (dispatch) => {
        return await new Promise((resolve, reject) => {
            if (ERR) reject(ERR)
            resolve(false)
        }).then(result => {
            dispatch({
                type: LOGOUT,
                payload: result,
            })
            return result
        }).catch(err => {
            dispatch({
                type: LOGOUT,
                payload: false,
            })
            return err
        })
    }
}
