import React, {useEffect} from 'react';
import Button from "@material-ui/core/Button";

function LoginButton({login, ...props}) {
    const onHandleLogin = () => {
        const popupWindow = window.open(process.env.REACT_APP_API_URL + '/api/auth/steam/handle', '', 'width=800, height=600');
        if (window.focus)
            popupWindow.focus()
    }

    const loginSubmit = (event) => {
        if (event.origin !== process.env.REACT_APP_API_URL) return;
        const {response: res} = event.data;

        // props.login(res.data)
        login(res.data)
    }

    useEffect(() => {
        // subscribe event
        window.addEventListener("message", loginSubmit);
        return () => {
            // unsubscribe event
            window.removeEventListener("messgae", loginSubmit);
        };
    }, []);

    return (
        <>
            <Button onClick={() => onHandleLogin()} color="inherit">Войти</Button>
        </>
    );
}

export default LoginButton
