import RCompThumbnail from "../../../assets/img/compositions/static.png";
import RCompPreview from "../../../assets/img/compositions/dynamic.gif";
import * as types from "./types";

export const initialState = {
    pageTitle: 'compositions',
    isFetching: false,
    compositions: [
        // {
        //     id: "79mWpD",
        //     title: 'Lespo',
        //     price: 3.43,
        //     thumbnail: RCompThumbnail,
        //     preview: RCompPreview,
        // }, {
        //     id: "weV2e3",
        //     title: 'Newzo',
        //     price: 2.43,
        //     thumbnail: RCompThumbnail,
        //     preview: RCompPreview,
        // }, {
        //     id: "P6UyyY",
        //     title: 'Winter',
        //     price: 412.43,
        //     thumbnail: RCompThumbnail,
        //     preview: RCompPreview,
        // },
    ],
}

function compositionsReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.FETCH_LIST:
            return {
                ...state,
                isFetching: true,
            }
        case types.FETCH_LIST_COMPLETED:
            payload.data.map(c => {
                return {
                    title: c.title,
                    link: c.link_alias,

                }
            })
            return {
                ...state,
                isFetching: false,
                compositions: [
                    ...payload.data
                ]
            }
        case types.FETCH_LIST_FAILED:
            return {
                ...state,
                isFetching: false,
                compositions: []
            }
        default:
            return state
    }
}

export default compositionsReducer
