import React from 'react';
import {connect} from "react-redux";
import Button from "@material-ui/core/Button";
import {login} from "../../../store/ducks/auth/operations";
import axios from "../../../store/utils/axios";
import {Link} from "@material-ui/core";

function LoginButton({login, ...props}) {
    const [steamLoginUrl, setSteamLoginUrl] = React.useState(null);

    React.useEffect(() => {
        axios.get('/api/login/steam')
            .then((response) => {
                if (response.status >= 200 && response.status <= 300) {
                    return response.data;
                }
                throw new Error('Something went wrong!');
            })
            .then((data) => {
                console.log(data.url)
                setSteamLoginUrl(data.url)
            })
            .catch((error) => setSteamLoginUrl(error));
    }, [steamLoginUrl]);

    return (
        <>
            {steamLoginUrl && (
                <Button href={steamLoginUrl} color="inherit">Войти</Button>
            )}
        </>
    )
}


export default connect(null, {login})(LoginButton)
