import * as types from "./types";

const initialState = {
    showErrorModal: false,
    errorMessage: ''
}

export default function errorReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.HTTP_404_ERROR:
            return execute404(state);
        case types.HTTP_500_ERROR:
            return execute500(state);
        case types.HTTP_OTHER_ERROR:
            return executeOtherError(state, payload);
        default:
            return state;
    }
};

const execute404 = (state) => {
    return {...state};
}

const execute500 = (state) => {
    return {...state};
}

const executeOtherError = (state, payload) => {
    return {
        ...state,
        showErrorModal: true,
        errorMessage: payload.error.response.data
    };
}
