import React from 'react';
import {makeStyles} from '@material-ui/core/styles';
import {withTheme} from '@material-ui/core/styles';

import Header from "../_includes/header/Header";
import Drawer from "../_includes/drawer/Drawer";
import Content from "../_includes/content/Content";
import Footer from "../_includes/footer/Footer";

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
        height: '100vh',
        flexDirection: 'column',
        flexGrow: 1,
    },
}));


function Layout({title='', ...props}) {
    const classes = useStyles();
    const theme = props.theme

    return (
        <div className={classes.root} style={{background: theme.palette.primary.mainGradient}}>
            <Header title={title}/>
            <Drawer/>
            <Content>{props.children}</Content>
            <Footer/>
        </div>
    );
}

export default withTheme(Layout)
