import React from 'react';
import {connect} from "react-redux";
import Button from "@material-ui/core/Button";
import {login} from "../../../store/ducks/auth/operations";
import axios from "../../../store/utils/axios";
import {Link} from "@material-ui/core";

class LoginButton extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            steamLoginUrl: null
        }
    }

    componentWillMount() {
        axios.get('/api/login/steam')
            .then((response) => {
                if (response.status >= 200 && response.status <= 300) {
                    return response.data;
                }
                throw new Error('Something went wrong!');
            })
            .then((data) => {
                console.log(data.url)
                this.setState({
                    steamLoginUrl: data.url
                })
            })
            .catch((error) => this.setState({
                steamLoginUrl: error
            }));
    }

    render() {
        return (
            <>
                {this.state.steamLoginUrl && (
                    <Button href={this.state.steamLoginUrl} color="inherit">Войти</Button>
                )}
            </>
        )

    }
}


export default connect(null, {login})(LoginButton)
