import React from 'react';
import Divider from "@material-ui/core/Divider";
import {makeStyles} from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";

import Header from "../_layouts/_includes/header/Header";
import Content from "../_layouts/_includes/content/Content";
import Footer from "../_layouts/_includes/footer/Footer";

import RSteamProfile from '../../../assets/img/aw_steam_profile.png'
import RAeWorkflow from '../../../assets/img/aw_ae_workflow.png'

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
    },
    wrapper: {
        display: 'flex',
        height: '100vh',
        flexDirection: 'column',
        flexGrow: 1,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
}));
const useLocalClasses = makeStyles(theme => ({
    papers: {
        display: 'flex',
        flexWrap: 'wrap',
        '& > *': {
            margin: theme.spacing(1),
            width: theme.spacing(16),
            height: theme.spacing(16),
        },
    },
}));


export default function Home(props) {
    const classes = useStyles();
    const cls = useLocalClasses();

    return (
        <div className={classes.root}>

            <Header/>

            <div className={classes.wrapper}>
                <Content>
                    <div>
                        <h1>Предложи свою идею для создания композиции или зацени уже имеющиеся!</h1>
                    </div>
                    <Divider variant="middle"/>
                    <main>
                        <div className={cls.papers}>
                            <Paper elevation={3}>
                                <img src={RSteamProfile} alt=""/>
                            </Paper>
                            <Paper elevation={3}>
                                <img src={RAeWorkflow} alt=""/>
                            </Paper>
                        </div>
                    </main>
                </Content>
                <Footer />
            </div>
        </div>
    );
}
