import React from 'react';
import {BrowserRouter as Router, Route, Switch} from "react-router-dom";
import CssBaseline from "@material-ui/core/CssBaseline";
import useMediaQuery from "@material-ui/core/useMediaQuery/useMediaQuery";
import createMuiTheme from "@material-ui/core/styles/createMuiTheme";
import {ThemeProvider} from '@material-ui/core/styles';
import {connect} from "react-redux";
import ProtectedRoute from "./ProtectedRoute";

import HomeContainer from "./HomeContainer";
import CompositionsPage from "../components/CompositionsPage/CompositionsPage";
import SuggestPage from "../components/SuggestPage/SuggestPage";
import NotFoundPage from "../components/ErrorPages/NotFoundPage/NotFoundPage";
import Background from "../components/_layouts/default/Background";

function App(props) {
    const prefersDarkMode = useMediaQuery(`(prefers-color-scheme: ${props.theme})`);
    const theme = React.useMemo(() => createMuiTheme({
        palette: {
            type: prefersDarkMode ? 'light' : 'dark',
            primary: {
                main: '#3f51b5',
                mainGradient: prefersDarkMode ?
                    "linear-gradient(0, rgb(238, 238, 238) 20%, rgb(46, 204, 113, .5) 50%, rgb(238, 238, 238) 100%)" :
                    "linear-gradient(0, rgb(66, 66, 66) 20%, rgb(155, 89, 182, .5) 50%, rgb(66, 66, 66) 100%)",
            },
            background: {
                default: prefersDarkMode ? '#eee' : '#424242',
            },
        },
    }), [prefersDarkMode]);

    return (
        <ThemeProvider theme={theme}>
            <CssBaseline/>
            <Router>
                <Background>
                    <Switch>
                        <Route exact path="/" component={HomeContainer}/>
                        <ProtectedRoute path="/compositions" component={CompositionsPage}/>
                        <Route path="/suggest" component={SuggestPage}/>
                        <Route path="*" component={NotFoundPage}/>
                    </Switch>
                </Background>
            </Router>
        </ThemeProvider>
    );
}

const mapStateToProps = (state) => {
    return {
        theme: state.layout.themeColor
    }
}

const mapDispatchToProps = (dispatch) => {
    return {}
}

export default connect(mapStateToProps, mapDispatchToProps)(App)
