import {
    authLogin,
    authLogout,
    authLoginCompleted,
    authLogoutCompleted,
    authLoginFailed,
    authLogoutFailed
} from "./actions";
import {fetch} from "../../utils";

const login = (params) => (dispatch) => {
    dispatch(authLogin())
    return fetch(`/airlock/csrf-cookie`, "GET")
        .then(() => {
            fetch(`/api/login/steam/callback${params}`, "GET")
                .then((user) => {
                    dispatch(authLoginCompleted(user))
                })
                .catch((error) => {
                    dispatch(authLoginFailed())
                });
        })
        .catch(err => {
            dispatch(authLoginFailed())
        });
}

const logout = () => dispatch => {
    dispatch(authLogout())
    return fetch(`/api/logout`, "POST")
        .then((res) => {
            dispatch(authLogoutCompleted())
        })
        .catch(err => {
            console.error(err)
            dispatch(authLogoutFailed())
            errorHandlerActions.handleHTTPError(error, props)
        });
}

const me = () => dispatch => {
    dispatch(authLogin())
    return fetch(`/api/user`, "GET")
        .then((response) => {
            dispatch(authLoginCompleted(response))
            return response
        })
        .catch((error) => {
            console.log('a')
            dispatch(logout())
        });
}


export {
    login, logout, me
}
