import React from 'react';
import {connect} from "react-redux";
import Button from "@material-ui/core/Button";
import {login} from "../../../store/ducks/auth/operations";
import {withSnackbar} from "notistack";

function LoginButton({login, ...props}) {
    React.useEffect(() => {
        axios.get('/api/login/steam')
            .then((response) => {
                if (response.status >= 200 && response.status <= 300) {
                    return response.data;
                }
                throw new Error('Something went wrong!');
            })
            .then((data) => this.setState({steamLoginUrl: data.url}))
            .catch((error) => console.error(error));
    }, [])

    const onHandleLogin = () => {

    }

    return (
        <Button onClick={() => onHandleLogin()} color="inherit">Войти</Button>
    );
}


export default connect(null, {login})(withSnackbar(LoginButton))
