import SecondaryLayout from "../../layout/SecondaryLayout";
import {Spacer} from "../../common/layout";
import {FormWrapper, InnerFormSpacer, Title, TypeVariants} from "../../common/form_components";
import React from "react";
import SuggestFormControls from "./Controls/Controls";
import Grid from "@material-ui/core/Grid";

const SuggestFormWrapper = ({caption = "Безымянный", size = "xl", children, ...props}) => {
    return (
        <>
            <Spacer/>
            <FormWrapper maxWidth={size}>
                <InnerFormSpacer>
                    <Title text={caption}/>
                    {children}
                </InnerFormSpacer>
            </FormWrapper>
        </>
    )
}


const SuggestForm = (props) => {
    const {
        values, touched, errors, suggestForm: {typeVariant, description},
        handleSubmit, handleReset, handleBack
    } = props;

    return (
        <SecondaryLayout>
            <SuggestFormWrapper caption="Письмо дизайнеру">
                <form noValidate onSubmit={handleSubmit}>
                    <Grid container>

                        {/*type variant*/}
                        <TypeVariants values={values}
                                      touched={touched} errors={errors}
                        />
                        {/*<TextInput*/}
                        {/*    required variant="outlined" fullWidth*/}
                        {/*    id="description" label="Описание"*/}
                        {/*    value={description} onChange={handleDesc}*/}
                        {/*    multiline*/}
                        {/*/>*/}

                    </Grid>
                    <SuggestFormControls onReset={handleReset} onBack={handleBack}/>
                </form>
            </SuggestFormWrapper>
        </SecondaryLayout>
    );
}

export default SuggestForm
