import React from 'react';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import FormGroup from "@material-ui/core/FormGroup";
import {CompVariantCheckbox} from "../../common/CompVariant";
import FormLabel from "@material-ui/core/FormLabel";
import Box from "@material-ui/core/Box";
import {TextInput} from "../../common/form_components";

function SuggestFormInputs(props) {

    const {handleDesc, handleTypeVariant} = props.handlers
    const {typeVariant, description} = props.inputs

    return (
        <Grid container spacing={4}>
            <Grid item xs={12}>
                <Box>
                    <FormLabel component="legend" style={{margin: '1rem 0',}}>Вариант отображения:</FormLabel>
                    <FormGroup row style={{justifyContent: 'center',}}>
                        <CompVariantCheckbox variant='long' handler={handleTypeVariant}
                                             checked={typeVariant.long.value}/>
                        <CompVariantCheckbox variant='short' handler={handleTypeVariant}
                                             checked={typeVariant.short.value}/>
                    </FormGroup>
                </Box>
            </Grid>
            <Grid item xs={12}>
                <Box mt={2}>
                    <TextInput
                        required variant="outlined" fullWidth
                        id="description" label="Описание"
                        value={description} onChange={handleDesc}
                    />
                </Box>
            </Grid>
            {/*<Grid item xs={12}>*/}
            {/*    <FormControlLabel*/}
            {/*        control={*/}
            {/*            <Switch checked={state.withBackground} onChange={handleChange('withBackground')}*/}
            {/*                    value="withBackground" inputProps={{'aria-label': 'default checkbox'}}*/}
            {/*                    color="default"*/}
            {/*            />*/}
            {/*        }*/}
            {/*        label="Фон"*/}
            {/*        labelPlacement="start"*/}
            {/*    />*/}
            {/*    {state.withBackground && (*/}
            {/*        <TextField required id="background" name="background" label="Адресная ссылка"*/}
            {/*                   fullWidth color="primary"*/}
            {/*        />*/}
            {/*    )}*/}
            {/*</Grid>*/}
        </Grid>
    )
}

export default SuggestFormInputs
