import SecondaryLayout from "../../layout/SecondaryLayout";
import {Preloader, Spacer} from "../../common/layout";
import {FormWrapper, InnerFormSpacer, PlaceholderList, Title, TypeVariants} from "../../common/form_components";
import React from "react";
import Grid from "@material-ui/core/Grid";
import OrderFormSummarize from "./Summarize/Summarize";
import OrderFormControls from "./Controls/Controls";

const OrderFormWrapper = ({caption = "Безымянный", size = "xl", children, ...props}) => {
    return (
        <SecondaryLayout>
            <Spacer/>
            <FormWrapper maxWidth={size}>
                <InnerFormSpacer>
                    <Title text={caption}/>
                    {children}
                </InnerFormSpacer>
            </FormWrapper>
        </SecondaryLayout>
    )
}


const OrderForm = (props) => {
    const {
        values, touched, errors, orderFormData: {typeVariant, price, ph, title, isFetching},
        handleSubmit, handleReset, handleBack
    } = props;

    return (
        isFetching ? <Preloader/> :
        <OrderFormWrapper caption="Форма заказа">
            <form onSubmit={handleSubmit} noValidate>
                <Grid container>

                    {/*type varient*/}
                    <TypeVariants
                        variant={typeVariant} values={values}
                        touched={touched} errors={errors}
                        isFetching={isFetching}
                    />
                    {/*inputs*/}
                    {!!values.ph && (
                        <PlaceholderList inputs={ph}/>
                    )}

                </Grid>
                <OrderFormSummarize price={price} title={title} isFetching={isFetching}/>
                <OrderFormControls onReset={handleReset} onBack={handleBack}/>
            </form>
        </OrderFormWrapper>
    );
}

export default OrderForm
