import React from 'react';
import Button from '@material-ui/core/Button';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Grid from '@material-ui/core/Grid';
import Typography from '@material-ui/core/Typography';
import {makeStyles} from '@material-ui/core/styles';
import Container from '@material-ui/core/Container';
import styled from 'styled-components'
import SecondaryLayout from "../_layouts/default/SecondaryLayout";

const useStyles = makeStyles(theme => ({
    heroContent: {
        padding: theme.spacing(3, 0, 3),
    },
    icon: {
        marginRight: theme.spacing(2),
    },
    heroButtons: {
        marginTop: theme.spacing(4),
    },
    card: {
        height: '100%',
        display: 'flex',
        flexDirection: 'column',
    },
    cardMedia: {
        paddingTop: '56.25%', // 16:9
    },
    cardContent: {
        flexGrow: 1,
    },
}));

const cards = [1, 2, 3, 4, 5, 6, 7];

function CompCard({...props}) {
    const classes = useStyles();

    return (
        <Grid item xs={12} sm={6} md={4}>
            <Card className={classes.card}>
                <CardMedia
                    className={classes.cardMedia}
                    image="https://source.unsplash.com/random"
                    title="Image title"
                />
                <CardContent className={classes.cardContent}>
                    <Typography gutterBottom variant="h5" component="h2">
                        Heading
                    </Typography>
                    <Typography>
                        This is a media card. You can use this section to describe the content.
                    </Typography>
                </CardContent>
                <CardActions>
                    <Button size="small" color="primary">
                        View
                    </Button>
                    <Button size="small" color="primary">
                        Edit
                    </Button>
                </CardActions>
            </Card>
        </Grid>
    )
}

const CompContainer = styled(Container)`
        padding-top: ${({theme}) => theme.spacing(8)};
        padding-bottom: ${({theme}) => theme.spacing(8)};
`

function Compositions({...props}) {
    const classes = useStyles()

    return (
        <SecondaryLayout>
            <div className={classes.heroContent}>
                <Container maxWidth="md">
                    <Typography component="h1" variant="h5" align="left" color="textPrimary">
                        Листинг композиций
                    </Typography>
                </Container>
            </div>
            <CompContainer maxWidth="md">
                <Grid container spacing={4}>
                    {cards.map(card => (
                        <CompCard key={card} />
                    ))}
                </Grid>
            </CompContainer>
        </SecondaryLayout>
    );
}

export default Compositions
