import React, {Component} from 'react';
import {connect} from "react-redux";
import SecondaryLayout from "../../layout/SecondaryLayout";
import {FormWrapper, InnerFormSpacer, Title} from "../../common/form_components";
import OrderFormInputs from "./Inputs/Inputs";
import OrderFormSummarize from "./Summarize/Summarize";
import OrderFormControls from "./Controls/Controls";
import {Spacer} from "../../common/layout";
import {withFormik} from 'formik';

const OrderFormContainer = (props) => {
    const {
        values, touched, errors,
        handleChange, handleBlur, handleSubmit,
    } = props;

    const handleTypeVariant = name => event => {
        // this.setState({
        //     ...this.state,
        //     typeVariant: {
        //         ...this.state.typeVariant,
        //         [name]: {
        //             ...this.state.typeVariant[name],
        //             value: event.target.checked,
        //         }
        //     }
        // });
    }

    const handleInputs = event => {
        // const inpName = event.target.id
        // const inpValue = event.target.value
        //
        // this.setState({
        //     ...this.state,
        //     inputs: {
        //         ...this.state.inputs,
        //         [inpName]: {
        //             ...this.state.inputs[inpName],
        //             value: inpValue
        //         }
        //     }
        // })

    }

    const onClear = () => {
        // setState(getDefaultState(props.compData))
        // clear session / localStorage (action)
    }
    const onBack = () => {
        props.history.goBack()
    }
    console.log(values)
    return (
        <SecondaryLayout>
            <Spacer/>

            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>
                    <Title text="Форма заказа"/>

                    <form onSubmit={handleSubmit}>
                        <input
                            type="text"
                            onChange={handleChange}
                            onBlur={handleBlur}
                            value={values.name}
                            name="name"
                        />
                        {errors.name && touched.name && <div id="feedback">{errors.name}</div>}
                    </form>

                </InnerFormSpacer>
            </FormWrapper>

        </SecondaryLayout>
    );
};

const getDefaultValues = (composition) => {
    let typeVariant = {
        long: composition.typeVariant === 1,
        short: composition.typeVariant === 2,
    }
    let inputs = {}

    if (Object.entries(composition.inputs).length > 0) {
        inputs = {
            ...composition.inputs
        }

        Object.keys(inputs).forEach((propName) => {
            inputs[propName] = {
                ...composition.inputs[propName],
                value: '',
            }
        })
    }

    return ({
        typeVariant,
        inputs,
    })
}

const OrderFormWithLocalState = withFormik({
    mapPropsToValues: (props) => getDefaultValues(props.compData),

    // Custom sync validation
    validate: values => {
        const errors = {};

        if (!values.name) {
            errors.name = 'Required';
        }

        return errors;
    },

    handleSubmit: (values, {setSubmitting}) => {
        setTimeout(() => {
            alert(JSON.stringify(values, null, 2));
            setSubmitting(false);
            // валидация данных в стейте, затем отправка на сервер
            // props.history.push(`${props.location.pathname}/success`)
        }, 1000);
    },

    displayName: 'BasicForm',
})(OrderFormContainer);

const mapStateToProps = (state) => ({
    user: state.auth,
    compData: state.compOrderForm
})

export default connect(mapStateToProps, {})(OrderFormWithLocalState)
// export default connect(mapStateToProps, {})(OrderFormContainer)




