import React from 'react';
import Button from "@material-ui/core/Button";

class LoginButton extends React.Component {
    onHandleLogin() {
        const popupWindow = window.open(process.env.REACT_APP_API_URL + '/api/auth/steam/handle', '', 'width=800, height=600');
        if (window.focus)
            popupWindow.focus()
    }

    loginSubmit(data) {
        this.props.login(data)
    }

    componentDidMount() {
        this.nv.addEventListener('message', event => {
            if (event.origin !== process.env.REACT_APP_API_URL) return;

            const {response: res} = event.data;
            this.loginSubmit(res.data)
        });
    }

    componentWillUnmount() {
        // Make sure to remove the DOM listener when the component is unmounted.
        this.nv.removeEventListener("nv-enter", this.handleNvEnter);
    }

    render() {
        return (
            <>
                <Button onClick={this.onHandleLogin} color="inherit">Войти</Button>
            </>
        );
    }
}

export default LoginButton
