import axios from 'axios'
import store from "../store";
import * as actions from "../ducks/auth/actions";

let token = document.head.querySelector('meta[name="csrf-token"]');
if (token) {
    axios.defaults.headers.common['X-Requested-With'] = 'XMLHttpRequest';
    axios.defaults.headers.common['X-CSRF-TOKEN'] = token.content;
}

export default (url, method, body) => {

    const options = {
        method,
        headers: requestHeaders(),
    };

    if (method === "POST") {
        return axios.post(url, JSON.stringify(body), options)
            .then(res => parseStatus(res.status, res.data))
            .catch(err => {console.log(err.data);parseStatus(err.status, err.data)})
    }
    if (method === "GET") {
        return axios.get(url, options)
            .then(res => parseStatus(res.status, res.data))
            .catch(err => parseStatus(err.status, err.data))
    }


    // .catch(err => console.log(err))
    // .then(res => {console.log(res)}, err => {console.log(err)})
};

function parseStatus(status, res) {
    return new Promise((resolve, reject) => {
        if (status >= 200 && status < 300) {
            resolve(res.data);
        } else {
            if (status === 401) {
                store.dispatch(actions.authLogout())
            }
            reject({status, res});
        }
    });
}

function requestHeaders() {
    const headers = {
        "Accept": "application/json",
        "Content-Type": "application/json",
    }

    // const jwt_token = localStorage.getItem('jwt_token')
    // if (jwt_token) headers["Authorization"] = `Bearer ${jwt_token}`

    return headers
}
