import React from 'react';
import {connect} from "react-redux";
import Button from "@material-ui/core/Button";
import {login} from "../../../store/ducks/auth/operations";
import {withSnackbar} from "notistack";


function LoginButton({login, ...props}) {
    function handler(event) {
        if (event.origin === process.env.REACT_APP_API_URL) {
            login(event.data)
                .then(res => {
                    console.log(res)
                })
                .catch((res) => {
                    props.enqueueSnackbar(res.message, {
                        variant: 'error',
                        autoHideDuration: 5000,
                    });
                })
            window.removeEventListener("message", handler)
        }
    }

    const onHandleLogin = () => {
        const popupWindow = window.open(process.env.REACT_APP_API_URL + '/api/auth/steam/handle', '_target', 'width=800, height=600');
        if (window.focus) popupWindow.focus();
        // subscribe event
        window.addEventListener("message", handler, false);
    }

    return (
        <Button onClick={() => onHandleLogin()} color="inherit">Войти</Button>
    );
}


export default connect(null, {login})(withSnackbar(LoginButton))
