import React from 'react';
import CssBaseline from "@material-ui/core/CssBaseline";
import {ThemeProvider as MuiThemeProvider} from '@material-ui/core';
import {ThemeProvider} from 'styled-components';
import {ApplyRootTheme} from "./views/layout/theme";
import Routes from "./routes";
import {login} from "./store/ducks/auth/actions";
import {connect} from "react-redux";


class Root extends React.Component {

    componentDidMount() {
        let state = localStorage["user"];
        if (state) {
            let authState = JSON.parse(state);
            login({
                isAuthenticated: authState.isAuthenticated,
                token: authState.token,
                data: authState.user
            })
        }
    }

    render() {
        const theme = ApplyRootTheme()

        return (
            <MuiThemeProvider theme={theme}>
                <ThemeProvider theme={theme}>
                    <CssBaseline/>
                    <Routes/>
                </ThemeProvider>
            </MuiThemeProvider>
        );
    }
}

export default connect(null, {login})(Root)
