import {authLogin, authLogout, authMe} from "./actions";
import {fetch} from "../../utils";

const login = (response) => {
    return dispatch => (
        new Promise((resolve, reject) => {
            if (response.success) {
                const {data: creds} = response
                dispatch(authLogin(creds));
                return resolve();
            } else {
                return reject();
            }
        })
    )

}

const logout = () => {
    return dispatch => (
        new Promise((resolve) => {
            fetch('/api/auth/logout', "POST").then(() => {
                dispatch(authLogout())
                return resolve()
            })
        })
    )
}

const me = () => {
    fetch('/api/auth/me', "POST").then((data) => {
        // console.log(data)
        // dispatch(authMe(data))
    }).catch(err => console.log(err))
}

export {
    login, logout, me
}
