import React from 'react';

import {withRouter} from "react-router-dom";
import Button from "@material-ui/core/Button";
import {login} from "../../../store/ducks/auth/actions";


class LoginButton extends React.Component {
    onHandleLogin() {
        const popupWindow = window.open(process.env.REACT_APP_API_URL + '/api/auth/steam/handle', '', 'width=800, height=600');
        if (window.focus)
            popupWindow.focus()
    }

    componentDidMount() {
        window.addEventListener('message', event => {
            if (event.origin !== process.env.REACT_APP_API_URL) return;

            const {response: res}= event.data;

            login(res)
            // if (res.success) {
            //     this.props.login(res.data).then(flag => {
            //         if (flag === true) {
            //             localStorage.setItem('authToken', res.data.api_token);
            //             this.props.history.push("/compositions")
            //         } // steam auth
            //     })
            // }
        });
    }

    render() {
        return (
            <Button onClick={this.onHandleLogin} color="inherit">Войти</Button>
        );
    }
}

export default withRouter(LoginButton)
