import React from 'react';
import {useStyles} from './Layout.style';
import {withTheme} from '@material-ui/core/styles';
import {connect} from "react-redux";

import Header from "../_includes/header/Header";
import Drawer from "../_includes/drawer/Drawer";
import Content from "../_includes/content/Content";
import Footer from "../_includes/footer/Footer";

function Layout({pageCaption = '', drawer = false, ...props}) {
    const classes = useStyles();
    const theme = props.theme

    return (
        <div className={classes.root} style={{background: theme.palette.primary.mainGradient}}>
            <Header user={props.user}/>
            {
                drawer && <Drawer/>
            }
            <Content>
                {props.children}
                <Footer phrases={props.phrases} />
            </Content>
        </div>
    );
}

const mapStateToProps = (store) => {
    return {
        phrases: store.layout.footer.phrases
    }
}

export default connect(mapStateToProps)(withTheme(Layout))
