import React from "react";
import CssBaseline from '@material-ui/core/CssBaseline';
import {LayoutUseStyles as useStyles} from "./LayoutUseStyles";

import AwBar from "./includes/AwBar";
import AwDrawer from "./includes/AwDrawer";
import Main from "./includes/Main";

export default function Layout(props) {
    const {location} = props;
    const classes = useStyles();
    const [open, setOpen] = React.useState(false);
    const [auth, setAuth] = React.useState(true);
    const [anchorEl, setAnchorEl] = React.useState(null);
    const isAnchored = Boolean(anchorEl);

    const handleDrawerOpen = () => {
        setOpen(true);
    };

    const handleDrawerClose = () => {
        setOpen(false);
    };

    const handleChange = event => {
        setAuth(event.target.checked);
    };

    const handleMenu = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    return (
        <div className={classes.root}>
            <CssBaseline/>
            <AwBar handleChange={handleChange} isAnchored={isAnchored} auth={auth} open={open} handleDrawerOpen={handleDrawerOpen}/>
            <AwDrawer open={open} handleDrawerClose={handleDrawerClose} />
            <Main open={open}>
                {props.children}
            </Main>
        </div>
    );
}
