import axios from 'axios'
import {refresh} from "../ducks/auth/operations";
import {authLogout} from "../ducks/auth/actions";
import store from '../store'

const fetch = (url, method, body) => {

    const options = {
        method,
        headers: requestHeaders(),
    };
    // console.log(body)

    if (method === "POST") {
        return axios.post(url, JSON.stringify(body), options)
            .then(res => parseStatus(res.status, res.data))
            .catch(err => parseStatus(err.response.status, err.response.data))
    }
    if (method === "GET") {
        return axios.get(url, options)
            .then(res => parseStatus(res.status, res.data))
            .catch(err => parseStatus(err.response.status, err.response.data))
    }
};

export default fetch

function parseStatus(status, res) {
    return new Promise((resolve, reject) => {
        if (status >= 200 && status < 300) {
            resolve(res.data);
        } else {
            if (status === 401 && store.getState().auth.isAuthenticated) {
                const jwtToken = localStorage.getItem('jwt_token')
                refresh(jwtToken)
            } else if (status === 401) {
                store.dispatch(authLogout())
            }
            reject({status, res});
        }
    });
}

function requestHeaders() {
    const headers = {
        "Accept": "application/json",
        "Content-Type": "application/json",
    }

    let token = document.head.querySelector('meta[name="csrf-token"]');
    if (!!token) {
        headers['X-Requested-With'] = 'XMLHttpRequest';
        headers['X-CSRF-TOKEN'] = token.content;
    }

    const jwt_token = localStorage.getItem('jwt_token')
    if (!!jwt_token) {
        headers["Authorization"] = `Bearer ${jwt_token}`
    }

    return headers
}
