import RAvatar from "../../assets/img/my_avatar.jpg"
import {AxiosInstance as Http} from "axios";

export const AUTH_LOGIN = 'AUTH_LOGIN';
export const AUTH_CHECK = 'AUTH_CHECK';
export const AUTH_LOGOUT = 'AUTH_LOGOUT';

const user = {
    user_id: null,
    steamid: null,
    nickname: null,
    avatar: null,
    balance: null,
    group: null,
    registered_at: null,
    visited_at: null,
};

const initialState = {
    isAuthenticated: false,
    isAdmin: false,
    user
};

export const authReducer = (state = initialState, {type, payload = null}) => {
    switch (type) {
        case AUTH_LOGIN:
            return authLogin(state, payload);
        case AUTH_CHECK:
            return checkAuth(state);
        case AUTH_LOGOUT:
            return logout(state);
        default:
            return state;
    }
};

const authLogin = (state, payload) => {
    const jwtToken = payload.token;
    const user = payload.user[0];
    localStorage.setItem('jwt_token', jwtToken);
    Http.defaults.headers.common['Authorization'] = `Bearer ${jwtToken}`;

    return {
        ...state,
        isAuthenticated: true,
        user
    }
};

const checkAuth = (state) => {
    state = {
        ...state,
        isAuthenticated: !!localStorage.getItem('jwt_token'),
    }

    if (state.isAuthenticated) {
        Http.defaults.headers.common['Authorization'] = `Bearer ${localStorage.getItem('jwt_token')}`;
    }

    return state;
};

const logout = (state) => {
    localStorage.removeItem('jwt_token');

    return {
        ...state,
        isAuthenticated: false,
        user
    }
};
