import React from 'react'
import {makeStyles} from "@material-ui/core/styles";

export const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
        flexWrap: 'wrap',
        minWidth: 300,
        width: '100%',
        justifyContent: 'center',
        margin: 'auto',
    },
    image: {
        position: 'relative',
        height: 300,
        minHeight: 100,
        maxHeight: 350,
        width: '25%',
        maxWidth: 450,
        boxShadow: '0 2px 2px 1px rgba(0, 0, 0, .95)',
        transition: 'all .15s ease-in-out',
        [theme.breakpoints.down('xs')]: {
            width: '100% !important', // Overrides inline-style
            height: 100,
        },
        '&:hover, &$focusVisible': {
            boxShadow: '0 5px 8px 2px rgba(0, 0, 0, .95)',
            zIndex: 1,
            '& $imageBackdrop': {
                opacity: 0.15,
            },
            '& $imageTitle': {
                border: '4px solid currentColor',
            },
        },
        '&:active': {
            color: 'rgba(255, 255, 255, .95)',
            boxShadow: '0 5px 8px 2px rgba(243, 156, 18, .95)',
            transition: 'all .75s ease-in-out',
        },
    },
    focusVisible: {},
    imageButton: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: theme.palette.common.white,
    },
    imageSrc: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        backgroundSize: 'cover',
        backgroundPosition: 'center 40%',
    },
    imageBackdrop: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        backgroundColor: theme.palette.common.black,
        opacity: 0.4,
        transition: theme.transitions.create('opacity'),
    },
    imageTitle: {
        position: 'relative',
        padding: `${theme.spacing(2)}px ${theme.spacing(4)}px ${theme.spacing(1) + 6}px`,
    }
}));
