import React from 'react';
import Divider from "@material-ui/core/Divider";
import {makeStyles} from "@material-ui/core/styles";
import Typography from "@material-ui/core/Typography";
import ButtonBase from "@material-ui/core/ButtonBase";

import Header from "../_layouts/_includes/header/Header";
import Content from "../_layouts/_includes/content/Content";
import Footer from "../_layouts/_includes/footer/Footer";

import RSteamProfile from '../../../assets/img/aw_steam_profile.png'
import RAeWorkflow from '../../../assets/img/aw_ae_workflow.png'

const images = [
    {
        url: RSteamProfile,
        title: 'Burgers',
        width: '30%',
    },
    {
        url: RAeWorkflow,
        title: 'Camera',
        width: '30%',
    },
];


const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
    },
    wrapper: {
        display: 'flex',
        height: '100vh',
        flexDirection: 'column',
        flexGrow: 1,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },

    image: {
        position: 'relative',
        height: 200,
        [theme.breakpoints.down('xs')]: {
            width: '100% !important', // Overrides inline-style
            height: 100,
        },
        '&:hover, &$focusVisible': {
            zIndex: 1,
            '& $imageBackdrop': {
                opacity: 0.15,
            },
            '& $imageMarked': {
                opacity: 0,
            },
            '& $imageTitle': {
                border: '4px solid currentColor',
            },
        },
    },
    focusVisible: {},
    imageButton: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        color: theme.palette.common.white,
    },
    imageSrc: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        backgroundSize: 'cover',
        backgroundPosition: 'center 40%',
    },
    imageBackdrop: {
        position: 'absolute',
        left: 0,
        right: 0,
        top: 0,
        bottom: 0,
        backgroundColor: theme.palette.common.black,
        opacity: 0.4,
        transition: theme.transitions.create('opacity'),
    },
    imageTitle: {
        position: 'relative',
        padding: `${theme.spacing(2)}px ${theme.spacing(4)}px ${theme.spacing(1) + 6}px`,
    },
    imageMarked: {
        height: 3,
        width: 18,
        backgroundColor: theme.palette.common.white,
        position: 'absolute',
        bottom: -2,
        left: 'calc(50% - 9px)',
        transition: theme.transitions.create('opacity'),
    },
}));

export default function Home(props) {
    const classes = useStyles();

    return (
        <div className={classes.root}>
            <Header/>

            <div className={classes.wrapper}>
                <Content>
                    <Typography align="center" variant="h4" gutterBottom>
                        Предложи свою идею для создания композиции или зацени уже имеющиеся!
                    </Typography>
                    <Divider variant="middle"/>
                    {images.map(image => (
                        <ButtonBase
                            focusRipple
                            key={image.title}
                            className={classes.image}
                            focusVisibleClassName={classes.focusVisible}
                            style={{
                                width: image.width,
                            }}
                        >
          <span
              className={classes.imageSrc}
              style={{
                  backgroundImage: `url(${image.url})`,
              }}
          />
                            <span className={classes.imageBackdrop}/>
                            <span className={classes.imageButton}>
            <Typography
                component="span"
                variant="subtitle1"
                color="inherit"
                className={classes.imageTitle}
            >
              {image.title}
                <span className={classes.imageMarked}/>
            </Typography>
          </span>
                        </ButtonBase>
                    ))}
                </Content>
                <Footer/>
            </div>
        </div>
    );
}
