import React from 'react';
import AppBar from '@material-ui/core/AppBar';
import Toolbar from '@material-ui/core/Toolbar';
import Button from '@material-ui/core/Button';
import Avatar from "@material-ui/core/Avatar";
import IconButton from "@material-ui/core/IconButton";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import RAvatar from "../../../../assets/img/my_avatar.jpg"
import {withRouter} from "react-router-dom";
import {useStyles} from "./Header.style";
import {connect} from "react-redux";
import {login, logout} from "../../../../store/actions/AuthActions";

import Logo from "./Logo";
import LoginButton from "../../../_common/LoginButton/LoginButton";

function Header(props) {
    const classes = useStyles();
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);
    const {userInfo, userActions} = props

    const handleMenu = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    return (
        <AppBar position="fixed" className={classes.appBar} color="default">
            <Toolbar disableGutters style={{padding: '0 4px',}}>
                <div className={classes.spacer}>
                    <Logo size={classes.medium}/>
                </div>
                {userInfo.isAuthenticated ? (
                        <>
                            <IconButton
                                aria-label="account of current user"
                                aria-controls="menu-appbar"
                                aria-haspopup="true"
                                onClick={handleMenu}
                                color="inherit"
                            >
                                <Avatar alt="dlinka_" src={RAvatar} className={classes.medium}/>
                            </IconButton>
                            <Menu
                                id="menu-appbar"
                                anchorEl={anchorEl}
                                keepMounted
                                elevation={0}
                                getContentAnchorEl={null}
                                anchorOrigin={{
                                    vertical: 'bottom',
                                    horizontal: 'right',
                                }}
                                transformOrigin={{
                                    vertical: 'top',
                                    horizontal: 'right',
                                }}
                                open={open}
                                onClose={handleClose}
                                style={{top: '5px'}}
                            >
                                <MenuItem onClick={handleClose}>Заказы</MenuItem>
                                <MenuItem onClick={handleClose}>Статистика</MenuItem>
                                <MenuItem onClick={handleClose}>Настройки</MenuItem>
                                <MenuItem onClick={() => {
                                    handleClose()
                                    userActions.logout().then(flag => {
                                        if (flag === true) props.history.push("/") // steam auth
                                    })
                                }}>Выйти</MenuItem>
                            </Menu>
                        </>
                    )
                    :
                    <LoginButton login={userActions.login} />
                }
            </Toolbar>
        </AppBar>
    );
}

const mapStateToProps = (store) => ({
    userInfo: store.user
})
const mapDispatchToProps = (dispatch) => ({
    userActions: {
        login: () => dispatch(login()),
        logout: () => dispatch(logout()),
    }
})

export default connect(mapStateToProps, mapDispatchToProps)(withRouter(Header))
