import {authLogin, authLogout} from "./actions";
import {fetch} from "../../utils";

const login = (params) => (dispatch) => {
    return fetch(`/airlock/csrf-cookie`, "GET")
        .then(() => {
            fetch(`/api/login/steam/callback${params}`, "GET")
                .then((user) => {
                    dispatch(authLogin(user))
                })
                .catch((error) => {
                    logout()
                });
        })
        .catch(err => console.error(err));
}

const logout = () => dispatch => {
    return fetch(`/api/logout`, "POST")
        .then((res) => {
            dispatch(authLogout())
        })
        .catch(err => console.error(err));
}

const me = () => dispatch => {
    return fetch(`/api/user`, "GET")
        .then((response) => {
            dispatch(authLogin(response))
            return response
        })
        .catch((error) => {
            dispatch(authLogout())
        });
}


export {
    login, logout, me
}
