import React from 'react'
import ButtonBase from "@material-ui/core/ButtonBase";
import {Link} from "react-router-dom";
import Typography from "@material-ui/core/Typography";
import useStyles from "./Home.style";
import styled from 'styled-components'

const BoxBtn = styled(ButtonBase)`
        will-change: transform;
        width: 450px;
        height: 300px;
        position: relative;
        margin: 3% 3%;
        box-shadow: 0 2px 2px 1px rgba(0, 0, 0, .95);
        transition: all .15s ease-in-out;
        transform: rotate(${({rotate}) => rotate}deg);
        
        &:active {
            color: rgba(255, 255, 255, .95);
            box-shadow: 0 5px 8px 2px rgba(243, 156, 18, .95);
            transition: all .75s ease-in-out;
        },
`

// imageSrc
const Image = styled.span`
        position: absolute;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        background-size: cover;
        background-position: center 40%;
        background-image: url('${({image}) => image}');
`

//imageBackdrop
const Backdrop = styled.span`
        position: absolute;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        opacity: 0.4;
`

const Title = styled.span`
        position: absolute;
        left: 0;
        right: 0;
        top: 0;
        bottom: 0;
        display: 'flex';
        align-items: center;
        justify-content: center;
`


const BoxVariant = React.memo(({image, ...props}) => {
    const classes = useStyles();

    return (
        <BoxBtn component={Link} to={image.to}
                className={classes.image}
                rotate={image.rotate}
                focusRipple
                p={5}
        >
            <Image image={image.url}/>
            <Backdrop className={classes.imageBackdrop}/>
            <Title className={classes.imageButton}>
            <Typography
                component="span"
                variant="subtitle1"
                color="inherit"
                className={classes.imageTitle}
            >
              {image.title}
            </Typography>
          </Title>
        </BoxBtn>
    )
})

export default BoxVariant
