import {AxiosInstance as Http} from "axios";
import * as types from "./types";

const user = {
    user_id: null,
    steamid: null,
    nickname: null,
    avatar: null,
    balance: null,
    group: null,
    registered_at: null,
    visited_at: null,
};

const initialState = {
    isAuthenticated: false,
    isAdmin: false,
    user
};

function authReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.AUTH_LOGIN:
            const jwtToken = payload.token;
            const user = payload.user[0];
            localStorage.setItem('jwt_token', jwtToken);
            Http.defaults.headers.common['Authorization'] = `Bearer ${jwtToken}`;

            return {
                ...state,
                isAuthenticated: true,
                user
            }
        case types.AUTH_CHECK:
            state = {
                ...state,
                isAuthenticated: !!localStorage.getItem('jwt_token'),
            }

            if (state.isAuthenticated) {
                Http.defaults.headers.common['Authorization'] = `Bearer ${localStorage.getItem('jwt_token')}`;
            }

            return state;
        case types.AUTH_LOGOUT:
            localStorage.removeItem('jwt_token');

            return {
                ...state,
                isAuthenticated: false,
                user
            }
        default:
            return state;
    }
};

export default authReducer
