import * as types from "./types";
import axios from 'axios'

const user = {
    user_id: null,
    steamid: null,
    nickname: null,
    avatar: null,
    balance: null,
    group: null,
    createdAt: null,
    visitedAt: null
};

const initialState = {
    isAuthenticated: false,
    user
};

export default function authReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.AUTH_LOGIN:
            return authLogin(state, payload);
        case types.AUTH_LOGOUT:
            return authLogout(state);
        case types.AUTH_ME:
            return authMe(state, payload);
        default:
            return state;
    }
};

const authLogin = (state, payload) => {
    const jwtToken = payload.token;
    const user = payload.user;
    localStorage.setItem('jwt_token', jwtToken);
    axios.defaults.headers.common['Authorization'] = `Bearer ${jwtToken}`;

    return {
        ...state,
        isAuthenticated: true,
        user: {
            ...user,
            createdAt: user.created_at,
            visitedAt: user.visited_at,
        }
    }
};

const authLogout = (state) => {
    localStorage.removeItem('jwt_token');

    return {
        ...state,
        isAuthenticated: false,
        user
    }
};

const authMe = (state, payload) => {
    const hasJwt = !!localStorage.getItem('jwt_token')
    state = {
        ...state,
    };

    if (hasJwt) {
        state.isAuthenticated = true

        if (!!payload) {
            state.user = {
                ...payload,
                createdAt: payload.created_at,
                visitedAt: payload.visited_at,
            }
        }
    }

    return state
};
