import React, {useMemo} from 'react';
import Copyright from "./Copyright";
import styled from "styled-components";
import { withTheme } from 'styled-components'

const StyledFooter = styled.div`
    position: absolute;
    left: 0;
    right: 0;
    bottom: 0;
    padding: 0;
    margin: 0;
    padding-top: 16px;
    padding: ${(props) => props.theme.spacing(3, 2) || "0"};
    padding-bottom: ${({theme}) => theme.spacing(1) || "0"};
    background: transparent;
    background-color: ${({theme}) => theme.palette.type === 'dark' ? theme.palette.grey[800] : theme.palette.grey[200]};
    text-align: left;
    width: auto;
    font-size: 12px;
    color: #8F98A0;
    border: 0;
`

const FooterContent = styled.div`
    position: relative;
    width: 960px;
    margin: 0 auto;
`

const FooterText = styled.span`
    padding: 2px 0 0 123px;
    line-height: 14px;
    display: block;
    color: #8F98A0;
`


const Footer = React.memo((props) => {

    return (
        <StyledFooter theme={props.theme}>
            <Copyright/>
        </StyledFooter>
    )
})

export default withTheme(Footer)
