import * as types from "./types";


export const initialState = {
    id: "79mWpD",
    title: "Lespo",
    typeVariant: 0,
    price: 3.43,
    ph: {
        username: {
            label: "Enter your username",
            rules: 'requiredd|max:30|min:2',
        },
        twitch: {
            label: "Your channel",
            rules: 'min:5',
        },
        facebook: {
            label: "Specify your facebook link",
            rules: 'required',
        },
    }
}

function compOrderFormReducer(state = initialState, {type, payload = null}) {
    switch (type) {
        case types.SET_COMPOSITION_FORM:
            return {
                ...state,
                form: {},
                isFetching: true,
                errors: {},
            }
        case types.SET_COMPOSITION_FORM_COMPLETED:
            return {
                ...state,
                form: {},
                isFetching: false,
                errors: {}
            }
        case types.SET_COMPOSITION_FORM_FAILED:
            return {
                ...state,
                form: {},
                isFetching: false,
                errors: {
                    message: payload.data.message,
                    status: payload.status
                }
            }
        default:
            return state
    }
}


export default compOrderFormReducer
