import {makeStyles} from "@material-ui/core/styles"

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },
    paper: {
        height: 140,
        width: 100,
        backgroundColor: '#ddd',
        margin: '0 auto 25px',
    },
    control: {
        padding: theme.spacing(2),
    },


    // pagination
    pagination: {
        boxSizing: 'border-box',
        lineHeight: 1,
        textAlign: 'center',
        transition: 'font-size .2s ease-in-out',
        fontSize: '12pt',

        listStyleType: 'none',
        display: 'inline',
        margin: 0,
        padding: '.5em',
    },

    // pageClassName
    item: {
        display: 'inline-block',
        fontSize: '100%',
        width: 'auto',
        borderRadius: 3,
    },

    itemLink: {
        
    }
}))

export default useStyles
