import * as types from './types'

export const fetchCompForm = (alias) => {
    return {
        type: types.SET_COMPOSITION_FORM,
        meta: {
            async: true,
            blocking: true,
            path: `/compositions/${alias}`,
            method: "GET",
        },
    }
};

export const makeOrderRequest = (data) => {
    return {
        type: types.MAKE_COMP_ORDER_REQUEST,
        meta: {
            async: true,
            blocking: true,
            path: `/compositions`,
            method: "POST",
            body: {"ay": 2}
        },
    }
}


export const clearCompForm = () => {
    return {
        type: types.CLEAR_COMP_FORM
    }
};
