import React from 'react'
import ReactPaginate from "react-paginate";
import useStyles from "./Compositions.style";

function Pager({pageCount, onPageChange, ...props}) {
    const classes = useStyles()

    return (
        <ReactPaginate
            previousLabel={'❮'} nextLabel={'❯'} breakLabel={'...'}
            marginPagesDisplayed={2} pageRangeDisplayed={5}
            pageCount={pageCount} onPageChange={onPageChange}
            containerClassName={classes.pagination} subContainerClassName={'pages pagination'}
            breakClassName={`${classes.breakMe} ${classes.item}`}
            activeLinkClassName={classes.itemLinkActive} disabledClassName={classes.itemDisabled}
            pageClassName={classes.item} pageLinkClassName={`${classes.itemLink} ${classes.paginationLink}`}
            nextClassName={classes.side} previousClassName={classes.side}
            previousLinkClassName={`${classes.sideLink} ${classes.paginationLink}`}
            nextLinkClassName={`${classes.sideLink} ${classes.paginationLink}`}
        />
    )
}

export default Pager
