import React from 'react';
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import PageCaptionSpacer from "../common/PageCaptionSpacer";
import SuggestFormInputs from "./SuggestFormInputs/SuggestFormInputs";
import {Btn, ButtonsGroup, FormWrapper, InnerFormSpacer, Title} from "../common/form_components";
import SuggestFormControls from "./SuggestFormControls/SuggestFormControls";

const getDefaultState = () => {
    let typeVariant = {
        long: {
            value: false,
        },
        short: {
            value: false
        },
    }

    let description = ''

    return ({
        typeVariant,
        description,
    })
}

function SuggestContainer(props) {

    const [state, setState] = React.useState(getDefaultState())

    const [value, setValue] = React.useState('');
    const [name, setName] = React.useState('');

    const handleTypeVariant = name => event => {
        setState({
            ...state,
            typeVariant: {
                ...state.typeVariant,
                [name]: {
                    ...state.typeVariant[name],
                    value: event.target.checked,
                }
            }
        });
    };
    const handleDesc = event => {
        setValue(event.target.value);
    };

    const onClear = () => {
        setState(getDefaultState())
        // clear session / localStorage (action)
    }
    const onBack = () => {
        props.history.goBack()
    }
    const onSubmit = e => {
        e.preventDefault();
        alert('ОК! Повесь обработчик на кнопку или вызови нужную функцию изнутри обработчика формы')
    }

    return (
        <SecondaryLayout>
            <PageCaptionSpacer/>

            <FormWrapper maxWidth="xl">
                <InnerFormSpacer>
                    <Title text="Письмо дизайнеру"/>

                    <form noValidate onSubmit={onSubmit}>
                        <SuggestFormInputs
                            inputs={state}
                            handlers={{handleTypeVariant, handleDesc}}
                        />

                        <SuggestFormControls onClear={onClear} onBack={onBack} />
                    </form>


                </InnerFormSpacer>
            </FormWrapper>

        </SecondaryLayout>
    );
}

export default SuggestContainer
