import React from 'react';
import IconButton from "@material-ui/core/IconButton";
import {Avatar as MaterialAvatar} from "@material-ui/core";
import Menu from "@material-ui/core/Menu";
import MenuItem from "@material-ui/core/MenuItem";
import {withRouter} from "react-router-dom";
import {connect} from "react-redux";
import {logout} from "../../../store/ducks/auth/operations";
import UserApi from '../../../store/utils/UserApi'
import LoginButton from "./LoginButton";
import Skeleton from "@material-ui/lab/Skeleton";
import styled from 'styled-components'

const Avatar = styled((props) => {
    return (
        (<IconButton
            aria-label="account of current user"
            aria-controls="menu-appbar"
            aria-haspopup="true"
            onClick={props.handler}
            color="inherit"
        >
            {props.isLoading ? (
                    <Skeleton wave={true} variant="circle" width={32} height={32}/>
                ) :
                (<MaterialAvatar
                    alt={props.nickname}
                    src={props.avatar}
                    style={{width: 32, height: 32,}}
                />)}
        </IconButton>)
    )
})``

function NavProfile({logout, ...props}) {
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);

    const handleMenu = event => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };


    // const ProfileCtx = (userInfo.isLoading ? <Skeleton wave={true} variant="circle" width={32} height={32} /> :
    //         (
    //             UserApi.isAuthenticated ? <NavProfile
    //                 avatar={userInfo.user.avatar}
    //                 nickname={userInfo.user.nickname}
    //             /> : <LoginButton/>
    //         )
    // )


    const menu = (<Menu
        id="menu-appbar" anchorEl={anchorEl} keepMounted elevation={0} getContentAnchorEl={null}
        anchorOrigin={{
            vertical: 'bottom',
            horizontal: 'right',
        }}
        transformOrigin={{
            vertical: 'top',
            horizontal: 'right',
        }}
        open={open} onClose={handleClose} style={{top: '5px'}}
    >
        {/*+(заказы, статистика, настройки)*/}
        <MenuItem onClick={() => {
            handleClose()
            logout()
        }}>Logout</MenuItem>
    </Menu>)


    // on logout
    if (UserApi.isAuthenticated && props.isLoading) {

    }

    // on staying logged in as always
    if (UserApi.isAuthenticated && !props.isLoading) {

    }

    // on login
    if (!UserApi.isAuthenticated && props.isLoading) {

    }

    // on guesting
    if (!UserApi.isAuthenticated && !props.isLoading) {

        {/*<Avatar*/}
        {/*    nickname={props.nickname} avatar={props.avatar} isLoading={props.isLoading}*/}
        {/*    handler={handleMenu}*/}
        {/*/>*/}

        {/*<LoginButton/>*/}
    }
}

export default connect(null, {logout})(withRouter(NavProfile))
