import React from 'react';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Switch from "@material-ui/core/Switch";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormGroup from "@material-ui/core/FormGroup";
import Checkbox from "@material-ui/core/Checkbox";
import LongCompIcon from "../../common/LongCompIcon";
import ShortCompIcon from "../../common/ShortCompIcon";
import LongCompDefault from '../../../assets/long_icon.svg'
import LongCompActive from '../../../assets/long_icon_active.svg'
import LongCompDisabled from '../../../assets/long_icon_disabled.svg'
import ShortCompDefault from '../../../assets/short_icon.svg'
import ShortCompActive from '../../../assets/short_icon_active.svg'
import ShortCompDisabled from '../../../assets/short_icon_disabled.svg'

function CompVariantImg({...props}) {
    return (
        <div></div>
    )
}

function CompVariantCb(props) {
    console.log(props)
    return (
<div>a</div>
    )
}

function CompVariant({variant, ...props}) {
    const labelName = variant === 'long' ? 'Длинный' : 'Короткий'

    return (
        <FormControlLabel
            control={
                <CompVariantCb value={variant} {...props} />
            }
            label={labelName}
            labelPlacement="bottom"
        />
    )
}

function SuggestForm(props) {
    const [state, setState] = React.useState({
        withBackground: false,
        long: false,
        short: false,
    });
    const [value, setValue] = React.useState('');
    const [name, setName] = React.useState('');


    const handleUsername = event => {
        setName(event.target.value);
    };

    const handleDesc = event => {
        setValue(event.target.value);
    };

    const handleChange = name => event => {
        setState({...state, [name]: event.target.checked});
    };

    return (
        <>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <TextField
                        required id="username" label="Никнейм"
                        fullWidth value={name} onChange={handleUsername}
                    />
                    <TextField
                        required id="description" label="Описание"
                        fullWidth value={value} onChange={handleDesc}
                        multiline
                    />
                </Grid>
                <Grid item xs={12}>
                    <FormGroup row>
                        <CompVariant variant='long' />
                        <CompVariant variant='short' />
                    </FormGroup>
                </Grid>
                <Grid item xs={12}>
                    <FormGroup row>
                        <FormControlLabel
                            control={
                                <Switch checked={state.withBackground} onChange={handleChange('withBackground')}
                                        value="withBackground" inputProps={{'aria-label': 'default checkbox'}}
                                        color="default"
                                />
                            }
                            label="Фон"
                        />
                        {state.withBackground && (
                            <TextField required id="background" name="background" label="Адресная ссылка"
                                       fullWidth color="primary"
                            />
                        )}
                    </FormGroup>
                </Grid>
            </Grid>
        </>
    )
}

export default SuggestForm
