import React from 'react';
import Divider from "@material-ui/core/Divider";
import {makeStyles} from "@material-ui/core/styles";
import Paper from "@material-ui/core/Paper";

import Header from "../_layouts/_includes/header/Header";
import Content from "../_layouts/_includes/content/Content";
import Footer from "../_layouts/_includes/footer/Footer";

import RSteamProfile from '../../../assets/img/aw_steam_profile.png'
import RAeWorkflow from '../../../assets/img/aw_ae_workflow.png'
import Container from "@material-ui/core/Container";
import Box from "@material-ui/core/Box";
import Typography from "@material-ui/core/Typography";

const useStyles = makeStyles(theme => ({
    root: {
        display: 'flex',
    },
    wrapper: {
        display: 'flex',
        height: '100vh',
        flexDirection: 'column',
        flexGrow: 1,
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
    footer: {
        padding: theme.spacing(3, 2),
        marginTop: 'auto',
        backgroundColor:
            theme.palette.type === 'dark' ? theme.palette.grey[800] : theme.palette.grey[200],
    },
}));
const useLocalClasses = makeStyles(theme => ({
    papers: {
        display: 'flex',
        flexWrap: 'wrap',
        '& > *': {
            margin: theme.spacing(1),
            width: theme.spacing(16),
            height: theme.spacing(16),
        },
    },
}));


export default function Home(props) {
    const classes = useStyles();
    const cls = useLocalClasses();

    return (
        <div className={classes.root}>

            <Header/>

            <div className={classes.wrapper}>
                <Content>
                    <div>
                        <h1>Предложи свою идею для создания композиции или зацени уже имеющиеся!</h1>
                    </div>
                    <Divider variant="middle"/>
                    <main>
                        <div className={cls.papers}>
                            <Paper elevation={3}>
                                <img src={RSteamProfile} alt=""/>
                            </Paper>
                            <Paper elevation={3}>
                                <img src={RAeWorkflow} alt=""/>
                            </Paper>
                        </div>
                    </main>
                </Content>
                <footer className={classes.footer}>
                    <Container maxWidth="sm">
                        <Box display="flex" justifyContent="center" alignItems="center">
                            <Typography variant="body1"><q>Продай разум и купи изумление, так как проницательность ума
                                является мыслью, а изумление — истинным видением.</q>&ndash;
                                <cite>Руми</cite>.
                            </Typography>
                        </Box>
                    </Container>
                    <Container maxWidth="sm">
                        <Box display="flex" justifyContent="center" alignItems="center">

                        </Box>
                    </Container>
                </footer>
            </div>
        </div>
    );
}
