import React from 'react';
import Paper from '@material-ui/core/Paper';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import SecondaryLayout from "../_layouts/default/SecondaryLayout";
import Container from "@material-ui/core/Container";
import styled from "styled-components";
import Switch from "@material-ui/core/Switch";
import FormControlLabel from "@material-ui/core/FormControlLabel";
import FormGroup from "@material-ui/core/FormGroup";
import Checkbox from "@material-ui/core/Checkbox";
import Icon from "@material-ui/core/Icon";
import LongCompDefault from '../../assets/long_icon.svg'
import ShortCompDefault from '../../assets/short_icon.svg'
import LongCompActive from '../../assets/long_icon_active.svg'
import ShortCompActive from '../../assets/short_icon_active.svg'

const Btn = styled(Button)`
    margin-top: ${({theme}) => theme.spacing(3)}px;
    margin-left: ${({theme}) => theme.spacing(1)}px;
`

const ButtonsGroup = styled.div`
        display: flex;
        justify-content: flex-end;
`

const InnerFormSpacer = styled(Paper)`
        margin-bottom: ${({theme}) => theme.spacing(3)}px;
        padding: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(3) * 2)} {
            margin-bottom: ${({theme}) => theme.spacing(6)}px;
            padding: ${({theme}) => theme.spacing(3)}px;
        },
`

const SuggestWrapper = styled(Container)`
        width: auto;
        margin-left: ${({theme}) => theme.spacing(2)}px;
        margin-right: ${({theme}) => theme.spacing(2)}px;
        ${({theme}) => theme.breakpoints.up(600 + theme.spacing(2) * 2)} {
            width: 600px;
            margin-left: auto;
            margin-right: auto;
        };
`

const PageCaptionSpacer = styled.div`
    padding: ${({theme}) => theme.spacing(4, 0)};
`


function LongCompIcon(props) {
    return (
        <Icon style={{textAlign: 'center', fontSize: '4rem',}}>
            <img {...props} height="100%" width="100%" alt="Long Composition" />
        </Icon>
    );
}

function ShortCompIcon(props) {
    return (
        <Icon style={{textAlign: 'center', fontSize: '4rem',}}>
            <img {...props} height="100%" width="100%" alt="Short Composition" />
        </Icon>
    );
}

function SuggestForm() {
    const [state, setState] = React.useState({
        withBackground: false,
        long: false,
        short: false,
    });
    const [value, setValue] = React.useState('');
    const [name, setName] = React.useState('');


    const handleUsername = event => {
        setName(event.target.value);
    };

    const handleDesc = event => {
        setValue(event.target.value);
    };

    const handleChange = name => event => {
        setState({...state, [name]: event.target.checked});
    };

    // description, type (two checkboxes), background url input (if checkbox true)
    return (
        <>
            <Grid container spacing={3}>
                <Grid item xs={12}>
                    <div>
                        <TextField
                            required id="username" label="Username"
                            fullWidth value={name} onChange={handleUsername}
                        />
                        <TextField
                            required id="description" label="Description"
                            fullWidth value={value} onChange={handleDesc}
                            multiline
                        />
                    </div>
                    <FormGroup row>
                        <FormControlLabel
                            control={
                                <Checkbox
                                    checked={state.long}
                                    icon={<LongCompIcon src={LongCompDefault}/>}
                                    checkedIcon={<LongCompIcon src={LongCompActive}/>}
                                    onChange={handleChange('long')} value="long"
                                    color="default"
                                    style={{border: '2px solid #fff',}}
                                />
                            }
                            label="Long"
                        />
                        <FormControlLabel
                            control={
                                <Checkbox
                                    checked={state.short}
                                    icon={<ShortCompIcon src={ShortCompDefault}/>}
                                    checkedIcon={<ShortCompIcon src={ShortCompActive}/>}
                                    onChange={handleChange('short')} value="short"
                                    color="default"
                                />
                            }
                            label="Long"
                        />
                    </FormGroup>
                    <FormGroup row>
                        <FormControlLabel
                            control={
                                <Switch checked={state.withBackground} onChange={handleChange('withBackground')}
                                        value="withBackground" inputProps={{'aria-label': 'default checkbox'}}
                                        color="default"
                                />
                            }
                            label="With steam background"
                        />
                        {state.withBackground && (
                            <TextField required id="background" name="background" label="Link Address"
                                       fullWidth autoComplete="Background" color="primary"
                            />
                        )}
                    </FormGroup>
                </Grid>
            </Grid>
        </>
    )
}

function SuggestContainer() {

    return (
        <SecondaryLayout>
            <PageCaptionSpacer>
                <Container maxWidth="xl">
                    <Typography component="h1" variant="h5" align="left" color="textPrimary">
                        Письмо дизайнерам
                    </Typography>
                </Container>
            </PageCaptionSpacer>

            <SuggestWrapper maxWidth="xl">
                <InnerFormSpacer>

                    <SuggestForm/>
                    <ButtonsGroup>
                        <Btn variant="contained">Предложить</Btn>
                    </ButtonsGroup>

                </InnerFormSpacer>
            </SuggestWrapper>

        </SecondaryLayout>
    );
}

export default SuggestContainer
