import React, {Component} from 'react';
import {connect} from "react-redux";
import {clearCompForm, fetchCompForm} from "../../../store/ducks/comp-order-form/actions";
import EnhancedForm from "./EnhancedForm";
import {Redirect} from "react-router-dom";


class CompFormContainer extends Component {

    componentDidMount() {
        const {fetchCompForm, match} = this.props
        const alias = match.params.alias
        fetchCompForm(alias)
    }

    componentWillUnmount() {
        const {clearCompForm} = this.props
        clearCompForm()
    }

    handleBack() {
        this.props.history.goBack()
    }

    render() {
        const {errors, isFetching} = this.props.orderFormData

        return (
            isFetching ? <div /> :
            !!errors.message ?
                <Redirect to={{
                    pathname: '/error',
                    state: {message: errors.message}
                }}
                /> : <EnhancedForm
                    orderFormData={this.props.orderFormData}
                    handleBack={this.handleBack.bind(this)}
                />
        )
    }
}


const mapStateToProps = (state) => ({
    orderFormData: state.compOrderForm
})

export default connect(mapStateToProps, {fetchCompForm, clearCompForm})(CompFormContainer)
