import React from 'react'
import styled from "styled-components";

const ContentWrapper = styled.div`
    position: relative;
    padding: 0 12px 64px 12px;
    width: 926px;
    margin: 0 auto;
    font-size: 13px;
    background: rgba(23, 23, 23, 0.85);
    margin-top: -16px;
    overflow: hidden;
    padding-top: 16px;
`

const Content = styled.div`
    position: relative;
`

const RightCol = styled.div`
    width: 268px;
    float: right;
`

const LeftCol = styled.div`
    width: 636px;
    float: left;
`

const CustomizationArea = styled.div`
`

const Customization = styled.div`
    position: relative;
    background-image: url('https://steamcommunity-a.akamaihd.net/public/images/profile/showcase_bg.png');
    background-position: top left;
    background-repeat: no-repeat;
    background-color: #141414;
    border-radius: 3px;
    padding: 0 10px 11px 10px;
    margin-bottom: 12px;
    font-size: 13px;
    overflow: hidden;
`

const CustomizationHeader = styled.div`
    font-weight: 200;
    color: #5491cf;
    font-size: 20px;
    line-height: 30px;
    overflow: hidden;
    white-space: nowrap;
    text-overflow: ellipsis;
`

const CustomizationBlock = styled.div`
`

const ShowcaseWrapper = styled.div`
`

const Primary = styled.div`
    display: block;
    float: left;
    position: relative;
    width: 508px;
    max-width: 83%;
`

const Secondary = styled.div`
    float: right;
    max-width: 17%;
    width: 102px;
    text-align: center;
`

const PrimarySlotLink = styled.span`
    cursor: pointer;
    border: 1px solid #000000;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    margin-bottom: 4px;
    
    &:hover {
        border: 1px solid #97C0E3;    
    }
`

const PrimarySlotImage = styled.img`
    display: block;
    max-width: 506px;
`

const SecondarySlot = styled.div`
    display: block;
    float: left;
    position: relative;
    float: none;
    margin-bottom: 11px;
`

const SecondarySlotLink = styled.span`
    cursor: pointer;
    border: 1px solid #000000;
    display: block;
    white-space: nowrap;
    overflow: hidden;
    
    &:hover {
        border: 1px solid #97C0E3;    
    }
`

const SecondarySlotImage = styled.img`
    display: block;
    max-width: 100px;
`

const Showcase = (props) => {
    return (
        <ShowcaseWrapper>
            <Primary>
                <PrimarySlotLink>
                    <PrimarySlotImage width="100%"
                                      src="https://steamuserimages-a.akamaihd.net/ugc/785249992931904478/33459A20BFFFF35CAF4B33E27C843663F6AA3FA5/?imw=506&&ima=fit&impolicy=Letterbox&imcolor=%23000000&letterbox=false"/>
                </PrimarySlotLink>
            </Primary>
            <Secondary>
                <SecondarySlot>
                    <SecondarySlotLink>
                        <SecondarySlotImage width="100%"
                                            src="https://steamuserimages-a.akamaihd.net/ugc/785249992931902062/6DD560C2E2299F3C1A5F0081009FA40BAB6DD63D/?imw=100&&ima=fit&impolicy=Letterbox&imcolor=%23000000&letterbox=false"/>
                    </SecondarySlotLink>
                </SecondarySlot>
            </Secondary>
            <div style={{clear: 'both',}}/>
        </ShowcaseWrapper>
    )
}

const ProfileContent = (props) => {
    return (
        <ContentWrapper>
            <Content>
                <RightCol/>
                <LeftCol>
                    <CustomizationArea>
                        <Customization>
                            <CustomizationHeader>Витрина иллюстраций</CustomizationHeader>
                            <CustomizationBlock>
                                <Showcase />
                            </CustomizationBlock>

                        </Customization>
                    </CustomizationArea>
                </LeftCol>
                <div style={{clear: 'both',}}/>
            </Content>
        </ContentWrapper>
    )
}

export default ProfileContent
