import {loginAC, logoutAC, setUserAC} from "./actions";

const login = (data) => {
    // if (data.success) {
    const {user} = data
    let userData = {
        user_id: user.user_id,
        steamid: user.steamid,
        nickname: user.nickname,
        avatar: user.avatar,
        balance: user.balance,
        group: user.group,
        timestamp: new Date().toString()
    };
    let authData = {
        isAuthenticated: true,
        token: data.token,
        user: userData
    }

    // save app state with user date in local storage
    localStorage["user"] = JSON.stringify(authData);
    return loginAC(authData)
    // } else alert("failed");
}

const logout = () => {
    // save app state with user date in local storage
    let authData = {
        isAuthenticated: false,
        token: null,
        user: {}
    }

    localStorage["user"] = JSON.stringify(authData);
    return logoutAC(authData)
}

const setLocalStorageUser = () => {
    let userStr = localStorage["user"];
    let user = JSON.parse(userStr);
    return setUserAC(user)
}


export {
    login, logout, setLocalStorageUser
}





const authLogin = (state,payload) => {
    const jwtToken = payload.token;
    const user = payload.user[0];
    if(!!payload.user[0].admin){
        localStorage.setItem('is_admin',true);
    } else {
        localStorage.setItem('is_admin',false);
    }
    localStorage.setItem('jwt_token',jwtToken);
    Http.defaults.headers.common['Authorization'] = `Bearer ${jwtToken}`;
    state = Object.assign({}, state, {
        isAuthenticated: true,
        isAdmin: localStorage.getItem('is_admin') === 'true',
        user
    });
    return state;

};
