import {makeStyles} from "@material-ui/core/styles"

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
    },

    compCard: {
        maxWidth: 154,
        border: '1px solid #fff',
        boxShadow: '0 2px 2px 1px rgba(0, 0, 0, .95)',
        '&:hover': {
            boxShadow: '0 5px 8px 2px rgba(0, 0, 0, .95)',
        },
    },

    paper: {
        display: 'flex',
        position: 'relative',
        backgroundColor: 'gray',
        margin: '0 auto 25px',
        height: 232,
        width: 154,
        flexDirection: 'column',
        '& img': {
            height: 140,
            width: 100,
        },
    },
    control: {
        padding: theme.spacing(2),
    },


    // pagination
    pagination: {
        boxSizing: 'border-box',
        lineHeight: 1,
        textAlign: 'left',
        transition: 'font-size .2s ease-in-out',
        fontSize: '12pt',

        listStyleType: 'none',
        display: 'inline',
        margin: 0,

        padding: '0.35em',
    },
    paginationLink: {
        cursor: 'pointer',
        outline: 'none',
        '-webkit-tap-highlight-color': 'transparent',
    },
    side: {
        display: 'inline-block',
        fontSize: '100%',
        width: 'auto',
        borderRadius: 3,
    },
    sideLink: {
        padding: '.5em',
    },
    item: {
        display: 'inline-block',
        fontSize: '100%',
        width: 'auto',
        borderRadius: 3,
    },
    itemLink: {
        color: '#777',
        fontSize: '100%',
        padding: '.5em',

        '&:focus, &:hover': {
            color: '#f60',
        },
    },
    itemLinkActive: {
        background: 'rgba(0,0,0,.1)',
    },
    itemDisabled: {
        opacity: .5,
        pointerEvents: 'none',
    },
}))

export default useStyles
