import React from 'react';
import Typography from '@material-ui/core/Typography';
import Container from '@material-ui/core/Container';
import Box from "@material-ui/core/Box";
import {useStyles} from "./Footer.style";
import {withStyles} from '@material-ui/styles';

function Copyright() {
    return (
        <Typography variant="body2" color="textSecondary">
            {'Copyright © '}

            <span>Artworch</span>{' '}

            {new Date().getFullYear()}
            {'.'}
        </Typography>
    );
}

class Footer extends React.Component() {

    constructor(props) {
        super(props)
        this.state = {
            phrases: [
                {
                    author: 'Руми',
                    text: "Продай разум и купи изумление, так как проницательность ума является мыслью, а изумление — истинным видением.",
                },
                {
                    author: 'Руми',
                    text: "Продай разум и купи изумление, так как проницательность ума является мыслью, а изумление — истинным видением.",
                },
                {
                    author: 'Руми',
                    text: "Продай разум и купи изумление, так как проницательность ума является мыслью, а изумление — истинным видением.",
                },
                {
                    author: 'Руми',
                    text: "Продай разум и купи изумление, так как проницательность ума является мыслью, а изумление — истинным видением.",
                },
                {
                    author: 'Руми',
                    text: "Продай разум и купи изумление, так как проницательность ума является мыслью, а изумление — истинным видением.",
                },
                {
                    author: 'Руми',
                    text: "Продай разум и купи изумление, так как проницательность ума является мыслью, а изумление — истинным видением.",
                }
            ],
            current: {
                author: 'Руми',
                text: "Продай разум и купи изумление, так как проницательность ума является мыслью, а изумление — истинным видением.",
            }
        }
    }


    render() {

        const {classes} = this.props

        return (
            <footer className={classes.footer}>
                <Container maxWidth="sm">
                    <Box display="flex" justifyContent="center" alignItems="center">
                        <Typography variant="body1"><q>{this.state.current.author}</q>&ndash;
                            <cite>{this.state.current.text}</cite>.
                        </Typography>
                    </Box>
                </Container>
                <Container maxWidth="sm">
                    <Box display="flex" justifyContent="center" alignItems="center">
                        <Copyright/>
                    </Box>
                </Container>
            </footer>
        )
    }
}

export default withStyles(useStyles())(Footer)
