import React from 'react';
import CssBaseline from "@material-ui/core/CssBaseline";
import {ThemeProvider as MuiThemeProvider} from '@material-ui/core';
import {ThemeProvider} from 'styled-components';
import {ApplyRootTheme} from "./views/layout/theme";
import Routes from "./routes";
import {setUser} from "./store/ducks/auth/actions";
import {connect} from "react-redux";
import {PagePreloader} from "./views/common/Preloader";


class Root extends React.Component {

    componentDidMount() {
        let state = localStorage["user"];
        if (state) {
            let authState = JSON.parse(state);
            this.props.setUser(authState)
        }
    }

    render() {
        const theme = ApplyRootTheme()

        return (
            <MuiThemeProvider theme={theme}>
                <ThemeProvider theme={theme}>
                    <CssBaseline/>

                    {!this.props.initialized ? <PagePreloader/> : <Routes/>}

                </ThemeProvider>
            </MuiThemeProvider>
        );
    }
}


const mapStateToProps = (state) => ({
    initialized: state.app.initialized
})

export default connect(mapStateToProps, {setUser})(Root)
